import axios from "axios";

const API_URL = "http://localhost:8080/api/records/"; // Update with your actual backend API URL

class TutorialDataService {
  getAll() {
    return axios.get(`${API_URL}/records`);
  }

  get(id) {
    return axios.get(`${API_URL}/records/${id}`);
  }

  create(data) {
    return axios.post(`${API_URL}/records`, data);
  }

  update(id, data) {
    return axios.put(`${API_URL}/records/${id}`, data);
  }

  delete(id) {
    return axios.delete(`${API_URL}/records/${id}`);
  }

  deleteAll() {
    return axios.delete(`${API_URL}/records`);
  }

  findByTitle(title) {
    return axios.get(`${API_URL}/records?customer_name=${title}`);
  }
}

export default new TutorialDataService();
