{customers.map((customer, index) => (
  <tr key={customer.sno}>
    <td>{(page - 1) * recordsPerPage + index + 1}</td>
    <td>{customer.customer_name}</td>
    <td>{customer.age}</td>
    <td>{customer.phone}</td>
    <td>{customer.location}</td>
    {/* Splitting created_at into date and time */}
    {sortBy === 'created_at' ? (
      Object.values(formatDateTime(customer.created_at)).map((value, i) => (
        <td key={i}>{value}</td>
      ))
    ) : (
      // Sort by time
      <td>{formatDateTime(customer.created_at).time}</td>
    )}
  </tr>
))}
