import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleSeedData = async () => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:8080/api/records/');
      setMessage(response.data.message);
    } catch (error) {
      setMessage('An error occurred while seeding data.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Seed Data</h1>
      <button onClick={handleSeedData} disabled={loading}>
        {loading ? 'Seeding Data...' : 'Seed 50 Records'}
      </button>
      {message && <p>{message}</p>}
    </div>
  );
}

export default App;
