import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS file for styling

function App() {
  const [customers, setCustomers] = useState([]);
  const [page, setPage] = useState(1);
  const [search, setSearch] = useState('');
  const [searchResults, setsearchReasults]= useState([]);
  const [totalPages, setTotalPages] = useState(0);
  const [sortBy, setSortBy] = useState('date'); // Default sort by date
  const recordsPerPage = 20;
  let data;
  let setSearchTerm;
  let setfilteredData;
  let searchTerm;
  let filteredData;
  let url = `http://localhost:8080/api/records?page=${page}&search=${search}&sortBy=${sortBy}`;
   useEffect(() => {
   fetchData();
   }, [page, search, sortBy]);

   const fetchData = async () => {
    try {
      let url = `http://localhost:8080/api/records?page=${page}&search=${search}&sortBy=${sortBy}`;
       if (search) {
         // If search is provided, add search filters for name and location columns
         url += `&name=${search}&location=${search}`;
      }

       const response = await axios.get(url);
      setCustomers(response.data.slice((page - 1) * recordsPerPage, page * recordsPerPage));
      setTotalPages(Math.ceil(response.data.length / recordsPerPage));
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  const SearchComponent = () => {
    const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setsearchReasults]= useState([]);
  const[filteredData, setfilteredData]=useState(data);
    }
      const handelSearch = (result) =>{
        setSearchTerm(result);
        const filteredData = data.filter(result=>
          result.customer_name.toLowerCase().includes(result.toLowerCase())||
          result.location.toLowerCase().includes(result.toLowerCase()));
          setfilteredData(filteredData)
        // try{
        //   const response = await axios.get(url);
        //   setsearchReasults(response.data);
        // }catch(error){
        //   console.error('Error fetching data:', error);
        // }
      };

  // Function to format date and time
  const formatDateTime = (dateTimeString) => {
    const dateTime = new Date(dateTimeString);
    const date = dateTime.toLocaleDateString();
    const time = dateTime.toLocaleTimeString();
    return { date, time };
  };

  const handleSort = (column) => {
    const sortedData = [...filteredData].sort((a,b) =>{
      if(column==='date'){
        return new Date(a.created_at) - new Date(b.created_at);
      }
      else if(column==='time'){
        return new Date(a.created_at).getTime() - new Date(b.created_at).getTime();
      }
    });
    setfilteredData(sortedData);
  }

  return (
    <div >
      <h1 className="heading">Customer Records</h1>
      <div className="search-container">
        <input
          type="text"
          placeholder="Search by Customer Name or Location"
          value={searchTerm}
          onChange={(e) => handelSearch(e.target.value)}
          className="search-input"
        />
        <button onClick={handelSearch} className="search-btn">
          Search
        </button>
        <div>
          {
            searchResults.map((result)=>{
              <div  key={result.customer_name}>

              </div>
            })
          }
        </div>
      </div>
      <div className="sorting-options">
        <button onClick={() => handleSort('date')} >
          Sort by Date
        </button>
        <button onClick={() => handleSort('time')} >
          Sort by Time
        </button>
      </div>
      <table className="customer-table">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Customer Name</th>
            <th>Age</th>
            <th>Phone</th>
            <th>Location</th>
            <th>Date</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer, index) => (
            <tr key={customer.sno}>
              <td>{(page - 1) * recordsPerPage + index + 1}</td>
              <td>{customer.customer_name}</td>
              <td>{customer.age}</td>
              <td>{customer.phone}</td>
              <td>{customer.location}</td>
              {/* Splitting created_at into date and time */}
              {Object.values(formatDateTime(customer.created_at)).map((value, i) => (
                <td key={i}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        <button disabled={page === 1} onClick={() => setPage(page - 1)} className="pagination-btn">
          Previous
        </button>
        <div className="page-numbers">
          Page {page} of {totalPages}
        </div>
        <button disabled={page === totalPages} onClick={() => setPage(page + 1)} className="pagination-btn">
          Next
        </button>
      </div>
    </div>
  );
}

export default App;
