import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [customers, setCustomers] = useState([]);
  const [page, setPage] = useState(1);
  const [search, setSearch] = useState('');
  const [totalPages, setTotalPages] = useState(0);
  const [sortBy, setSortBy] = useState('date');
  const [sortedData, setSortedData] = useState([]);
  const [sortOrder, setSortOrder] = useState('desc');
  const recordsPerPage = 20;

  useEffect(() => {
    fetchData();
  }, [page, search, sortBy, sortOrder]);

  // const fetchData = async () => {
  //   try {
  //     let url = `http://localhost:8080/api/records?page=${page}&search=${search}&sortBy=${sortBy}`;
  //     if (search) {
  //       url += `&customer_name=${search}&location=${search}`;
  //     }
  //     const response = await axios.get(url);
  //      const slicedData = response.data.slice((page - 1) * recordsPerPage, page * recordsPerPage);

  //     setCustomers(slicedData);
  //     // setSortedData(sortData(response.data, sortBy, sortOrder));
  //     setTotalPages(Math.ceil(response.data.length / recordsPerPage));
  //   } catch (error) {
  //     console.error('Error fetching data:', error);
  //   }
  // };
  const fetchData = async () => { 
    try { 
      let url = `http://localhost:8080/api/records?page=${page}&search=${search}&sortBy=${sortBy}`; 
      if (search) { 
        // If search is provided, add search filters for name and location columns 
        url += `&name=${search}&location=${search}`; 
      } 
      const response = await axios.get(url); 
      setCustomers(response.data.slice((page - 1) * recordsPerPage, page * recordsPerPage)); 
      setTotalPages(Math.ceil(response.data.length / recordsPerPage)); 
    } catch (error) { 
      console.error('Error fetching data:', error); 
    } 
  };

  const sortData = (data, sortBy, order) => {
    const sorted = [...data].sort((a, b) => {
      if (sortBy === 'date') {
        const dateA = new Date(a.created_at);
        const dateB = new Date(b.created_at);
        return order === 'asc' ? dateA - dateB : dateB - dateA;
      } else if (sortBy === 'time') {
        const timeA = new Date(a.created_at).getTime();
        const timeB = new Date(b.created_at).getTime();
        return order === 'asc' ? timeA - timeB : timeB - timeA;
      }
      return 0;
    });

    return sorted;
  };

  const handleSortByDate = () => {
    const newOrder = sortOrder === 'asc' ? 'desc' : 'asc';
    setSortOrder(newOrder);
    setSortBy('date');
    setSortedData(sortData(customers, 'date', newOrder));
  };

  const handleSortByTime = () => {
    const newOrder = sortOrder === 'asc' ? 'desc' : 'asc';
    setSortOrder(newOrder);
    setSortBy('time');
    setSortedData(sortData(customers, 'time', newOrder));
  };

  const formatDate = (dateTimeString) => {
    const date = new Date(dateTimeString);
    return date.toLocaleDateString();
  };

  const formatTime = (dateTimeString) => {
    const time = new Date(dateTimeString);
    return time.toLocaleTimeString();
  };

  return (
    <div className="container">
      <h1 className="heading">Customer Records</h1>
      <div className="search-container">
        <input
          type="text"
          placeholder="Search by Customer Name or Location"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-input"
        />
        <button onClick={fetchData} className="search-btn">
          Search
        </button>
      </div>

      <div className="sorting-options">
        <button onClick={handleSortByDate} className={`sort-btn ${sortBy === 'date' ? 'active' : ''}`}>
          Sort by Date
        </button>
        <button onClick={handleSortByTime} className={`sort-btn ${sortBy === 'time' ? 'active' : ''}`}>
          Sort by Time
        </button>
      </div>

      <table className="customer-table">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Customer Name</th>
            <th>Age</th>
            <th>Phone</th>
            <th>Location</th>
            <th>Date</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {sortedData.map((row, index) => (
            <tr key={index}>
              <td>{(page - 1) * recordsPerPage + index + 1}</td>
              <td>{row.customer_name}</td>
              <td>{row.age}</td>
              <td>{row.phone}</td>
              <td>{row.location}</td>
              <td>{formatDate(row.created_at)}</td>
              <td>{formatTime(row.created_at)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="pagination">
        <button disabled={page === 1} onClick={() => setPage(page - 1)} className="pagination-btn">
          Previous
        </button>
        <div className="page-numbers">
          Page {page} of {totalPages}
        </div>
        <button disabled={page === totalPages} onClick={() => setPage(page + 1)} className="pagination-btn">
          Next
        </button>
      </div>
    </div>
  );
}

export default App;
