import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";

export default class AddTutorial extends Component {
  constructor(props) {
    super(props);
    this.seedData = this.seedData.bind(this);

    this.state = {
      loading: false,
      message: ""
    };
  }

  seedData() {
    this.setState({ loading: true });
    TutorialDataService.seedData()
      .then(response => {
        this.setState({ message: response.data.message });
      })
      .catch(error => {
        console.error("Error seeding data:", error);
        this.setState({ message: "An error occurred while seeding data." });
      })
      .finally(() => {
        this.setState({ loading: false });
      });
  }

  render() {
    return (
      <div className="submit-form">
        <h1>Seed Data</h1>
        <button onClick={this.seedData} disabled={this.state.loading}>
          {this.state.loading ? "Seeding Data..." : "Seed 50 Records"}
        </button>
        {this.state.message && <p>{this.state.message}</p>}
      </div>
    );
  }
}
